import React from 'react';
import { motion } from 'framer-motion';
import Header from './Header';
import ParticleBackground from './ParticleBackground';

interface LayoutProps {
  children: React.ReactNode;
}

const Layout: React.FC<LayoutProps> = ({ children }) => {
  return (
    <div className="min-h-screen bg-gaming-dark text-white relative overflow-hidden">
      {/* Background Effects */}
      <div className="fixed inset-0 bg-gaming-gradient"></div>

      {/* Particle Background */}
      <ParticleBackground />

      {/* Animated Background Particles */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        {/* Floating orbs */}
        {[...Array(6)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute w-2 h-2 bg-neon-blue rounded-full opacity-30"
            animate={{
              x: [0, 100, -50, 150, 0],
              y: [0, -100, 50, -75, 0],
              scale: [1, 1.5, 0.8, 1.2, 1],
            }}
            transition={{
              duration: 15 + i * 2,
              repeat: Infinity,
              ease: "easeInOut",
              delay: i * 2,
            }}
            style={{
              left: `${10 + i * 15}%`,
              top: `${20 + i * 10}%`,
            }}
          />
        ))}

        {/* Gradient overlays */}
        <div className="absolute top-0 left-0 w-1/3 h-1/3 bg-gradient-to-br from-neon-purple/20 via-transparent to-transparent blur-3xl"></div>
        <div className="absolute bottom-0 right-0 w-1/3 h-1/3 bg-gradient-to-tl from-neon-blue/20 via-transparent to-transparent blur-3xl"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-1/2 h-1/2 bg-gradient-radial from-neon-pink/10 via-transparent to-transparent blur-3xl"></div>
      </div>

      {/* Grid Pattern Overlay */}
      <div 
        className="fixed inset-0 opacity-5 pointer-events-none"
        style={{
          backgroundImage: `
            linear-gradient(rgba(0, 245, 255, 0.3) 1px, transparent 1px),
            linear-gradient(90deg, rgba(0, 245, 255, 0.3) 1px, transparent 1px)
          `,
          backgroundSize: '20px 20px'
        }}
      />

      {/* Main Content */}
      <div className="relative z-10 flex flex-col min-h-screen">
        <Header />

        <main className="flex-1 container mx-auto px-4 py-8">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.3 }}
          >
            {children}
          </motion.div>
        </main>

        {/* Footer */}
        <footer className="relative z-10 border-t border-white/10 py-6">
          <div className="container mx-auto px-4">
            <div className="flex flex-col md:flex-row items-center justify-between space-y-4 md:space-y-0">
              <div className="flex items-center space-x-6 text-sm text-gray-400">
                <span>&copy; 2024 Shard Flip</span>
                <a 
                  href="#" 
                  className="hover:text-neon-blue transition-colors"
                >
                  Terms
                </a>
                <a 
                  href="#" 
                  className="hover:text-neon-blue transition-colors"
                >
                  Privacy
                </a>
              </div>

              <div className="flex items-center space-x-4">
                <span className="text-sm text-gray-400">Powered by</span>
                <div className="flex items-center space-x-2">
                  <div className="w-6 h-6 bg-gradient-to-r from-neon-blue to-neon-purple rounded-full flex items-center justify-center">
                    <span className="text-xs font-bold text-white">S</span>
                  </div>
                  <span className="text-sm font-semibold text-neon-blue">Shardeum</span>
                </div>
              </div>
            </div>
          </div>
        </footer>
      </div>

      {/* Loading overlay for page transitions */}
      <motion.div
        className="fixed inset-0 bg-gaming-dark z-50 flex items-center justify-center pointer-events-none"
        initial={{ opacity: 1 }}
        animate={{ opacity: 0 }}
        transition={{ duration: 0.8, delay: 0.2 }}
        onAnimationComplete={() => {
          // Remove from DOM after animation
        }}
      >
        <div className="text-center">
          <motion.div
            className="w-16 h-16 border-4 border-neon-blue/30 border-t-neon-blue rounded-full mx-auto mb-4"
            animate={{ rotate: 360 }}
            transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
          />
          <motion.h2
            className="text-2xl font-gaming font-bold text-gradient"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.3 }}
          >
            SHARD FLIP
          </motion.h2>
          <motion.p
            className="text-gray-400 mt-2"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.5 }}
          >
            Loading the game...
          </motion.p>
        </div>
      </motion.div>
    </div>
  );
};

export default Layout;
