import React from 'react';
import { motion } from 'framer-motion';
import Header from './Header';
import ParticleBackground from './ParticleBackground';

interface LayoutProps {
  children: React.ReactNode;
}

const Layout: React.FC<LayoutProps> = ({ children }) => {
  return (
    <div className="min-h-screen text-white relative overflow-hidden">
      {/* Enhanced Animated Grid Overlay */}
      <div className="grid-overlay"></div>

      {/* Particle Background */}
      <ParticleBackground />

      {/* Enhanced Animated Background Particles */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        {/* Colorful floating orbs */}
        {[...Array(8)].map((_, i) => (
          <motion.div
            key={i}
            className={`absolute w-3 h-3 rounded-full opacity-40 ${
              i % 4 === 0 ? 'bg-neon-blue shadow-lg shadow-neon-blue/50' :
              i % 4 === 1 ? 'bg-neon-purple shadow-lg shadow-neon-purple/50' :
              i % 4 === 2 ? 'bg-neon-pink shadow-lg shadow-neon-pink/50' :
              'bg-neon-green shadow-lg shadow-neon-green/50'
            }`}
            animate={{
              x: [0, Math.sin(i) * 200, -Math.cos(i) * 150, Math.sin(i + 1) * 180, 0],
              y: [0, -Math.cos(i) * 180, Math.sin(i) * 120, -Math.sin(i + 1) * 140, 0],
              scale: [1, 1.8, 0.6, 1.4, 1],
              rotate: [0, 180, 360, 540, 720],
            }}
            transition={{
              duration: 18 + i * 3,
              repeat: Infinity,
              ease: "easeInOut",
              delay: i * 1.5,
            }}
            style={{
              left: `${5 + i * 12}%`,
              top: `${15 + i * 8}%`,
            }}
          />
        ))}

        {/* Enhanced gradient overlays with animation */}
        <motion.div 
          className="absolute top-0 left-0 w-2/5 h-2/5 bg-gradient-to-br from-neon-purple/15 via-neon-blue/10 to-transparent blur-3xl"
          animate={{
            scale: [1, 1.2, 0.8, 1.1, 1],
            opacity: [0.15, 0.25, 0.1, 0.2, 0.15],
          }}
          transition={{
            duration: 12,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />
        <motion.div 
          className="absolute bottom-0 right-0 w-2/5 h-2/5 bg-gradient-to-tl from-neon-green/15 via-neon-yellow/10 to-transparent blur-3xl"
          animate={{
            scale: [1, 0.9, 1.3, 0.95, 1],
            opacity: [0.15, 0.3, 0.08, 0.22, 0.15],
          }}
          transition={{
            duration: 14,
            repeat: Infinity,
            ease: "easeInOut",
            delay: 2,
          }}
        />
        <motion.div 
          className="absolute top-1/3 left-1/4 w-1/2 h-1/2 bg-gradient-radial from-neon-orange/12 via-neon-pink/8 to-transparent blur-3xl"
          animate={{
            x: [0, 50, -30, 40, 0],
            y: [0, -40, 60, -20, 0],
            scale: [1, 1.4, 0.7, 1.2, 1],
            opacity: [0.12, 0.2, 0.05, 0.18, 0.12],
          }}
          transition={{
            duration: 16,
            repeat: Infinity,
            ease: "easeInOut",
            delay: 4,
          }}
        />
      </div>

      {/* Main Content */}
      <div className="relative z-10 flex flex-col min-h-screen">
        <Header />

        <main className="flex-1 container mx-auto px-4 py-8">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.3 }}
          >
            {children}
          </motion.div>
        </main>

        {/* Footer */}
        <footer className="relative z-10 border-t border-white/10 py-6">
          <div className="container mx-auto px-4">
            <div className="flex flex-col md:flex-row items-center justify-between space-y-4 md:space-y-0">
              <div className="flex items-center space-x-6 text-sm text-gray-400">
                <span>&copy; 2024 Shard Flip</span>
                <a 
                  href="#" 
                  className="hover:text-neon-blue transition-colors"
                >
                  Terms
                </a>
                <a 
                  href="#" 
                  className="hover:text-neon-blue transition-colors"
                >
                  Privacy
                </a>
              </div>

              <div className="flex items-center space-x-4">
                <span className="text-sm text-gray-400">Powered by</span>
                <div className="flex items-center space-x-2">
                  <div className="w-6 h-6 bg-gradient-to-r from-neon-blue to-neon-purple rounded-full flex items-center justify-center">
                    <span className="text-xs font-bold text-white">S</span>
                  </div>
                  <span className="text-sm font-semibold text-neon-blue">Shardeum</span>
                </div>
              </div>
            </div>
          </div>
        </footer>
      </div>

      {/* Loading overlay for page transitions */}
      <motion.div
        className="fixed inset-0 bg-gaming-dark z-50 flex items-center justify-center pointer-events-none"
        initial={{ opacity: 1 }}
        animate={{ opacity: 0 }}
        transition={{ duration: 0.8, delay: 0.2 }}
        onAnimationComplete={() => {
          // Remove from DOM after animation
        }}
      >
        <div className="text-center">
          <motion.div
            className="w-16 h-16 border-4 border-neon-blue/30 border-t-neon-blue rounded-full mx-auto mb-4"
            animate={{ rotate: 360 }}
            transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
          />
          <motion.h2
            className="text-2xl font-gaming font-bold text-gradient"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.3 }}
          >
            SHARD FLIP
          </motion.h2>
          <motion.p
            className="text-gray-400 mt-2"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.5 }}
          >
            Loading the game...
          </motion.p>
        </div>
      </motion.div>
    </div>
  );
};

export default Layout;
